import React, { useState, useRef } from 'react';
import { Square, Circle, Type, Download, Trash2, RotateCw, Plus, X, Upload } from 'lucide-react';

// Image Upload Panel Component
function ImageUploadPanel({ onClose, onImageAdd }) {
  const [imageUrl, setImageUrl] = useState('');
  const [numberValue, setNumberValue] = useState('');
  const fileInputRef = useRef(null);

  const handleFileUpload = (e) => {
    const file = e.target.files[0];
    if (file && file.type.startsWith('image/')) {
      const reader = new FileReader();
      reader.onload = (event) => {
        setImageUrl(event.target.result);
      };
      reader.readAsDataURL(file);
    }
  };

  const handleAddImage = () => {
    if (imageUrl) {
      onImageAdd(imageUrl, numberValue);
      setImageUrl('');
      setNumberValue('');
    }
  };

  return (
    <div className="w-80 bg-white border-r border-gray-200 p-4 flex flex-col">
      <div className="flex justify-between items-center mb-4">
        <h3 className="font-semibold">Bib Maker Image Upload</h3>
        <button
          onClick={onClose}
          className="p-1 hover:bg-gray-100 rounded"
        >
          <X className="w-4 h-4" />
        </button>
      </div>

      <div className="flex-1 space-y-4">
        {/* Upload Area */}
        <div className="border-2 border-dashed border-gray-300 rounded-lg p-6 text-center">
          <input
            ref={fileInputRef}
            type="file"
            accept="image/*"
            onChange={handleFileUpload}
            className="hidden"
          />

          {!imageUrl ? (
            <div className="space-y-3">
              <Upload className="w-12 h-12 mx-auto text-gray-400" />
              <div>
                <button
                  onClick={() => fileInputRef.current?.click()}
                  className="px-4 py-2 bg-blue-500 text-white rounded-lg hover:bg-blue-600"
                >
                  Choose Image
                </button>
              </div>
              <p className="text-xs text-gray-500">
                PNG, JPG, GIF up to 10MB
              </p>
            </div>
          ) : (
            <div className="space-y-3">
              <img
                src={imageUrl}
                alt="Preview"
                className="max-w-full max-h-48 mx-auto rounded"
              />
              <button
                onClick={() => fileInputRef.current?.click()}
                className="px-3 py-1 text-sm bg-gray-200 text-gray-700 rounded hover:bg-gray-300"
              >
                Change Image
              </button>
            </div>
          )}
        </div>

        {/* Number Field */}
        <div>
          <label className="block text-sm font-medium mb-2">
            Number Value
          </label>
          <input
            type="text"
            value={numberValue}
            onChange={(e) => setNumberValue(e.target.value)}
            placeholder="Enter a number (e.g., 100, 3.14)"
            className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
          />
          <p className="text-xs text-gray-500 mt-1">
            Optional: Associate a number with this image
          </p>
        </div>

        {/* Add to Canvas Button */}
        <button
          onClick={handleAddImage}
          disabled={!imageUrl}
          className="w-full px-4 py-3 bg-green-500 text-white rounded-lg hover:bg-green-600 disabled:opacity-50 disabled:cursor-not-allowed flex items-center justify-center gap-2"
        >
          <Plus className="w-5 h-5" />
          Add to Canvas
        </button>

        {/* Instructions */}
        <div className="text-xs text-gray-600 p-3 bg-blue-50 rounded-lg">
          <p className="font-medium mb-1">How to use:</p>
          <ul className="list-disc list-inside space-y-1">
            <li>Upload an image from your device</li>
            <li>Optionally enter a number value</li>
            <li>Click "Add to Canvas" to place it</li>
            <li>Drag and resize on the canvas</li>
          </ul>
        </div>
      </div>
    </div>
  );
}

// Variable Panel Component
function VariablePanel({ variables, onUpdate, onDelete, onAdd, onClose }) {
  const [formula, setFormula] = useState('');
  const [copied, setCopied] = useState(false);

  const formatFormula = (text) => {
    // Replace common formula patterns with formatted versions
    let formatted = text;

    // Convert ^ to superscript notation
    const superscripts = {
      '0': '⁰', '1': '¹', '2': '²', '3': '³', '4': '⁴',
      '5': '⁵', '6': '⁶', '7': '⁷', '8': '⁸', '9': '⁹'
    };

    formatted = formatted.replace(/\^(\d+)/g, (match, num) => {
      return num.split('').map(digit => superscripts[digit] || digit).join('');
    });

    // Convert common symbols
    formatted = formatted.replace(/\*/g, '×');
    formatted = formatted.replace(/\//g, '÷');
    formatted = formatted.replace(/<=/g, '≤');
    formatted = formatted.replace(/>=/g, '≥');
    formatted = formatted.replace(/!=/g, '≠');
    formatted = formatted.replace(/sqrt\(/g, '√(');

    return formatted;
  };

  const handleCopyFormula = () => {
    const formatted = formatFormula(formula);
    navigator.clipboard.writeText(formatted).then(() => {
      setCopied(true);
      setTimeout(() => setCopied(false), 2000);
    });
  };

  return (
    <div className="w-64 bg-white border-r border-gray-200 p-4 flex flex-col">
      <div className="flex justify-between items-center mb-4">
        <h3 className="font-semibold">Variables</h3>
        <button
          onClick={onClose}
          className="p-1 hover:bg-gray-100 rounded"
        >
          <X className="w-4 h-4" />
        </button>
      </div>

      <div className="text-xs text-gray-600 mb-3 p-2 bg-blue-50 rounded">
        Use variables in text as <code className="bg-white px-1 rounded">{'{VariableName}'}</code>
      </div>

      <div className="flex-1 overflow-y-auto space-y-4">
        {/* Variables List */}
        <div className="space-y-2">
          {variables.map(variable => (
            <div key={variable.id} className="border border-gray-200 rounded-lg p-2">
              <div className="flex items-center gap-2 mb-2">
                <input
                  type="text"
                  value={variable.name}
                  onChange={(e) => onUpdate(variable.id, 'name', e.target.value)}
                  className="flex-1 px-2 py-1 text-sm border border-gray-300 rounded focus:outline-none focus:ring-2 focus:ring-blue-500"
                  placeholder="Variable name"
                />
                <button
                  onClick={() => onDelete(variable.id)}
                  className="p-1 hover:bg-red-100 rounded text-red-600"
                >
                  <X className="w-4 h-4" />
                </button>
              </div>
              <input
                type="text"
                value={variable.value}
                onChange={(e) => onUpdate(variable.id, 'value', e.target.value)}
                className="w-full px-2 py-1 text-sm border border-gray-300 rounded focus:outline-none focus:ring-2 focus:ring-blue-500"
                placeholder="Value"
              />
            </div>
          ))}
        </div>

        {/* Formula Formatter */}
        <div className="border-t border-gray-200 pt-4">
          <h4 className="text-sm font-semibold mb-2">Formula Formatter</h4>
          <div className="text-xs text-gray-600 mb-2 p-2 bg-green-50 rounded">
            Type formulas with * / ^ symbols. They'll be formatted when copied.
          </div>

          <textarea
            value={formula}
            onChange={(e) => setFormula(e.target.value)}
            placeholder="E.g., F = m * a^2"
            className="w-full px-2 py-2 text-sm border border-gray-300 rounded focus:outline-none focus:ring-2 focus:ring-blue-500 resize-none"
            rows="3"
          />

          {formula && (
            <div className="mt-2 p-2 bg-gray-50 rounded text-sm">
              <div className="text-xs text-gray-500 mb-1">Preview:</div>
              <div className="font-mono">{formatFormula(formula)}</div>
            </div>
          )}

          <button
            onClick={handleCopyFormula}
            disabled={!formula}
            className="mt-2 w-full px-3 py-2 bg-green-500 text-white rounded-lg hover:bg-green-600 disabled:opacity-50 disabled:cursor-not-allowed text-sm"
          >
            {copied ? '✓ Copied!' : 'Copy Formatted'}
          </button>
        </div>
      </div>

      <button
        onClick={onAdd}
        className="mt-4 w-full px-4 py-2 bg-blue-500 text-white rounded-lg hover:bg-blue-600 flex items-center justify-center gap-2"
      >
        <Plus className="w-4 h-4" />
        Add Variable
      </button>
    </div>
  );
}

// Properties Panel Component
function PropertiesPanel({ element, onUpdate }) {
  if (!element) return null;

  return (
    <div className="w-64 bg-white border-l border-gray-200 p-4">
      <h3 className="font-semibold mb-4">Properties</h3>

      <div className="space-y-4">
        <div>
          <label className="block text-sm font-medium mb-1">Color</label>
          <input
            type="color"
            value={element.color}
            onChange={(e) => onUpdate(element.id, { color: e.target.value })}
            className="w-full h-10 rounded cursor-pointer"
          />
        </div>

        <div>
          <label className="block text-sm font-medium mb-1">Width</label>
          <input
            type="range"
            min="50"
            max="400"
            value={element.width}
            onChange={(e) => onUpdate(element.id, { width: parseInt(e.target.value) })}
            className="w-full"
          />
          <span className="text-sm text-gray-600">{element.width}px</span>
        </div>

        <div>
          <label className="block text-sm font-medium mb-1">Height</label>
          <input
            type="range"
            min="50"
            max="400"
            value={element.height}
            onChange={(e) => onUpdate(element.id, { height: parseInt(e.target.value) })}
            className="w-full"
          />
          <span className="text-sm text-gray-600">{element.height}px</span>
        </div>

        {element.type === 'text' && (
          <div>
            <label className="block text-sm font-medium mb-1">Font Size</label>
            <input
              type="range"
              min="12"
              max="72"
              value={element.fontSize || 24}
              onChange={(e) => onUpdate(element.id, { fontSize: parseInt(e.target.value) })}
              className="w-full"
            />
            <span className="text-sm text-gray-600">{element.fontSize || 24}px</span>
          </div>
        )}

        {element.type === 'image' && element.numberValue && (
          <div className="p-3 bg-gray-50 rounded-lg">
            <label className="block text-sm font-medium mb-1">Associated Number</label>
            <div className="text-lg font-semibold text-blue-600">{element.numberValue}</div>
          </div>
        )}
      </div>
    </div>
  );
}

export default function CanvaStarter() {
  const [elements, setElements] = useState([]);
  const [selectedId, setSelectedId] = useState(null);
  const [dragging, setDragging] = useState(null);
  const [draggingBadge, setDraggingBadge] = useState(null);
  const [variables, setVariables] = useState([
    { id: 1, name: 'Force', value: '100N' },
    { id: 2, name: 'Mass', value: '50kg' }
  ]);
  const [showVariablePanel, setShowVariablePanel] = useState(false);
  const [showImagePanel, setShowImagePanel] = useState(true);
  const canvasRef = useRef(null);

  // Replace variables in text with their values
  const replaceVariables = (text) => {
    let result = text;
    variables.forEach(variable => {
      const regex = new RegExp(`\\{${variable.name}\\}`, 'g');
      result = result.replace(regex, variable.value);
    });
    return result;
  };

  const addVariable = () => {
    const newVariable = {
      id: Date.now(),
      name: 'NewVar',
      value: '0'
    };
    setVariables([...variables, newVariable]);
  };

  const updateVariable = (id, field, value) => {
    setVariables(variables.map(v => 
      v.id === id ? { ...v, [field]: value } : v
    ));
  };

  const deleteVariable = (id) => {
    setVariables(variables.filter(v => v.id !== id));
  };

  const addElement = (type) => {
    const newElement = {
      id: Date.now(),
      type,
      x: 100,
      y: 100,
      width: type === 'text' ? 200 : 100,
      height: type === 'text' ? 50 : 100,
      rotation: 0,
      color: type === 'text' ? '#000000' : '#3b82f6',
      text: type === 'text' ? 'Type {Force} here' : null,
      fontSize: 24
    };
    setElements([...elements, newElement]);
    setSelectedId(newElement.id);
  };

  const addImageElement = (imageUrl, numberValue) => {
    const newElement = {
      id: Date.now(),
      type: 'image',
      x: 100,
      y: 100,
      width: 200,
      height: 200,
      rotation: 0,
      imageUrl: imageUrl,
      numberValue: numberValue
    };
    setElements([...elements, newElement]);
    setSelectedId(newElement.id);
  };

  const deleteElement = () => {
    if (selectedId) {
      const currentIndex = elements.findIndex(el => el.id === selectedId);
      const filteredElements = elements.filter(el => el.id !== selectedId);
      setElements(filteredElements);

      // Keep focus on another element if available
      if (filteredElements.length > 0) {
        // Try to select the next element, or the previous one if we deleted the last
        const nextIndex = currentIndex < filteredElements.length ? currentIndex : filteredElements.length - 1;
        setSelectedId(filteredElements[nextIndex].id);
      } else {
        setSelectedId(null);
      }
    }
  };

  const rotateElement = () => {
    if (selectedId) {
      setElements(elements.map(el => 
        el.id === selectedId ? { ...el, rotation: (el.rotation + 45) % 360 } : el
      ));
    }
  };

  const updateElement = (id, updates) => {
    setElements(elements.map(el => el.id === id ? { ...el, ...updates } : el));
  };

  const handleMouseDown = (e, element) => {
    e.stopPropagation();
    // e.preventDefault();
    setSelectedId(element.id);
    const rect = canvasRef.current.getBoundingClientRect();
    setDragging({
      id: element.id,
      offsetX: e.clientX - rect.left - element.x,
      offsetY: e.clientY - rect.top - element.y
    });
  };

  const handleElementClick = (e, element) => {
    e.stopPropagation();
    setSelectedId(element.id);
  };

  const handleMouseMove = (e) => {
    if (dragging) {
      const rect = canvasRef.current.getBoundingClientRect();
      updateElement(dragging.id, {
        x: e.clientX - rect.left - dragging.offsetX,
        y: e.clientY - rect.top - dragging.offsetY
      });
    } else if (draggingBadge) {
      const rect = canvasRef.current.getBoundingClientRect();
      const element = elements.find(el => el.id === draggingBadge.id);
      if (element) {
        const elementScreenX = element.x + rect.left;
        const elementScreenY = element.y + rect.top;

        updateElement(draggingBadge.id, {
          badgeX: e.clientX - elementScreenX - draggingBadge.offsetX,
          badgeY: e.clientY - elementScreenY - draggingBadge.offsetY
        });
      }
    }
  };

  const handleMouseUp = () => {
    setDragging(null);
    setDraggingBadge(null);
  };

  const handleBadgeMouseDown = (e, element) => {
    e.stopPropagation();
    e.preventDefault();

    const rect = canvasRef.current.getBoundingClientRect();
    const elementScreenX = element.x + rect.left;
    const elementScreenY = element.y + rect.top;
    const currentBadgeX = element.badgeX || 10;
    const currentBadgeY = element.badgeY || 10;

    setDraggingBadge({
      id: element.id,
      offsetX: e.clientX - elementScreenX - currentBadgeX,
      offsetY: e.clientY - elementScreenY - currentBadgeY
    });
  };

  const exportCanvas = () => {
    const canvas = document.createElement('canvas');
    canvas.width = 800;
    canvas.height = 600;
    const ctx = canvas.getContext('2d');

    ctx.fillStyle = '#ffffff';
    ctx.fillRect(0, 0, canvas.width, canvas.height);

    const drawPromises = elements.map(el => {
      return new Promise((resolve) => {
        ctx.save();
        ctx.translate(el.x + el.width / 2, el.y + el.height / 2);
        ctx.rotate((el.rotation * Math.PI) / 180);
        ctx.translate(-el.width / 2, -el.height / 2);

        if (el.type === 'rectangle') {
          ctx.fillStyle = el.color;
          ctx.fillRect(0, 0, el.width, el.height);
          ctx.restore();
          resolve();
        } else if (el.type === 'circle') {
          ctx.fillStyle = el.color;
          ctx.beginPath();
          ctx.arc(el.width / 2, el.height / 2, el.width / 2, 0, Math.PI * 2);
          ctx.fill();
          ctx.restore();
          resolve();
        } else if (el.type === 'text') {
          ctx.fillStyle = el.color;
          ctx.font = `${el.fontSize || 24}px Arial`;
          ctx.textBaseline = 'top';

          const displayText = replaceVariables(el.text);
          const lines = displayText.split('\n');

          lines.forEach((line, index) => {
            ctx.fillText(line, 10, 10 + (index * (el.fontSize || 24) * 1.2));
          });
          ctx.restore();
          resolve();
        } else if (el.type === 'image') {
          const img = new Image();
          img.onload = () => {
            ctx.drawImage(img, 0, 0, el.width, el.height);
            ctx.restore();
            resolve();
          };
          img.onerror = () => {
            ctx.restore();
            resolve();
          };
          img.src = el.imageUrl;
        }
      });
    });

    Promise.all(drawPromises).then(() => {
      const link = document.createElement('a');
      link.download = 'engineering-diagram.png';
      link.href = canvas.toDataURL();
      link.click();
    });
  };

  const togglePanel = (panelType) => {
    if (panelType === 'variables') {
      setShowVariablePanel(true);
      setShowImagePanel(false);
    } else if (panelType === 'image') {
      setShowImagePanel(true);
      setShowVariablePanel(false);
    }
  };

  const selectedElement = elements.find(el => el.id === selectedId);

  return (
    <div className="flex h-screen bg-gray-100">
      {/* Toolbar */}
      <div className="w-20 bg-white border-r border-gray-200 flex flex-col items-center py-4 gap-4">
        <button
          onClick={() => addElement('rectangle')}
          className="p-3 hover:bg-gray-100 rounded-lg transition-colors"
          title="Add Rectangle"
        >
          <Square className="w-6 h-6" />
        </button>
        <button
          onClick={() => addElement('circle')}
          className="p-3 hover:bg-gray-100 rounded-lg transition-colors"
          title="Add Circle"
        >
          <Circle className="w-6 h-6" />
        </button>
        <button
          onClick={() => addElement('text')}
          className="p-3 hover:bg-gray-100 rounded-lg transition-colors"
          title="Add Text"
        >
          <Type className="w-6 h-6" />
        </button>
        <button
          onClick={() => togglePanel('image')}
          className={`p-3 rounded-lg transition-colors ${showImagePanel ? 'bg-blue-100' : 'hover:bg-gray-100'}`}
          title="Upload Image"
        >
          <Upload className="w-6 h-6" />
        </button>
        <div className="flex-1" />
        <button
          onClick={exportCanvas}
          className="p-3 hover:bg-gray-100 rounded-lg transition-colors"
          title="Export PNG"
        >
          <Download className="w-6 h-6" />
        </button>
      </div>

      {/* Image Upload Panel - Now shown by default */}
      {showImagePanel && (
        <ImageUploadPanel
          onClose={() => setShowImagePanel(false)}
          onImageAdd={addImageElement}
        />
      )}

      {/* Variable Panel - Hidden by default */}
      {showVariablePanel && (
        <VariablePanel
          variables={variables}
          onUpdate={updateVariable}
          onDelete={deleteVariable}
          onAdd={addVariable}
          onClose={() => setShowVariablePanel(false)}
        />
      )}

      {/* Canvas */}
      <div className="flex-1 flex flex-col">
        <div className="bg-white border-b border-gray-200 p-4 flex gap-2">
          {!showImagePanel && (
            <button
              onClick={() => togglePanel('image')}
              className="px-4 py-2 bg-blue-500 text-white rounded-lg hover:bg-blue-600"
            >
              Show Image Upload
            </button>
          )}
          {!showVariablePanel && (
            <button
              onClick={() => togglePanel('variables')}
              className="px-4 py-2 bg-gray-200 text-gray-700 rounded-lg hover:bg-gray-300"
            >
              Show Variables
            </button>
          )}
          <button
            onClick={rotateElement}
            disabled={!selectedId}
            className="px-4 py-2 bg-blue-500 text-white rounded-lg hover:bg-blue-600 disabled:opacity-50 disabled:cursor-not-allowed flex items-center gap-2"
          >
            <RotateCw className="w-4 h-4" />
            Rotate
          </button>
          <button
            onClick={deleteElement}
            disabled={!selectedId}
            className="px-4 py-2 bg-red-500 text-white rounded-lg hover:bg-red-600 disabled:opacity-50 disabled:cursor-not-allowed flex items-center gap-2"
          >
            <Trash2 className="w-4 h-4" />
            Delete
          </button>
        </div>

        <div className="flex-1 flex items-center justify-center p-8">
          <div
            ref={canvasRef}
            className="relative bg-white shadow-lg"
            style={{ width: 800, height: 600 }}
            onMouseMove={handleMouseMove}
            onMouseUp={handleMouseUp}
            onMouseLeave={handleMouseUp}
            onClick={() => setSelectedId(null)}
          >
            {elements.map(element => (
              <div
                key={element.id}
                className={`absolute cursor-move ${selectedId === element.id ? 'ring-2 ring-blue-500' : ''}`}
                style={{
                  left: element.x,
                  top: element.y,
                  width: element.width,
                  height: element.height,
                  transform: `rotate(${element.rotation}deg)`,
                  transformOrigin: 'center'
                }}
                onMouseDown={(e) => handleMouseDown(e, element)}
                onClick={(e) => handleElementClick(e, element)}
              >
                {element.type === 'rectangle' && (
                  <div
                    className="w-full h-full"
                    style={{ backgroundColor: element.color }}
                  />
                )}
                {element.type === 'circle' && (
                  <div
                    className="w-full h-full rounded-full"
                    style={{ backgroundColor: element.color }}
                  />
                )}
                {element.type === 'text' && (
                  <div className="relative w-full h-full">
                    <textarea
                      value={element.text}
                      onChange={(e) => updateElement(element.id, { text: e.target.value })}
                      className="w-full h-full font-bold border-none outline-none bg-transparent resize-none"
                      style={{ 
                        color: element.color,
                        fontSize: `${element.fontSize || 24}px`
                      }}
                      onClick={(e) => e.stopPropagation()}
                    />
                    <div 
                      className="absolute inset-0 pointer-events-none font-bold whitespace-pre-wrap"
                      style={{ 
                        color: element.color,
                        opacity: 0.6,
                        backgroundColor: 'rgba(255,255,255,0.9)',
                        fontSize: `${element.fontSize || 24}px`
                      }}
                    >
                      {replaceVariables(element.text)}
                    </div>
                  </div>
                )}
                {element.type === 'image' && (
                  <div className="w-full h-full relative">
                    <img
                      src={element.imageUrl}
                      alt="Uploaded"
                      className="w-full h-full object-contain"
                    />
                    {element.numberValue && (
                      <div 
                        className="absolute bg-blue-500 text-white px-2 py-1 rounded text-sm font-semibold cursor-move hover:bg-blue-600 hover:shadow-lg transition-all"
                        style={{
                          left: `${element.badgeX || 10}px`,
                          top: `${element.badgeY || 10}px`
                        }}
                        onMouseDown={(e) => handleBadgeMouseDown(e, element)}
                        title="Drag to reposition badge"
                      >
                        {element.numberValue}
                      </div>
                    )}
                  </div>
                )}
              </div>
            ))}
          </div>
        </div>
      </div>

      {/* Properties Panel */}
      <PropertiesPanel
        element={selectedElement}
        onUpdate={updateElement}
      />
    </div>
  );
}
